mapper.select:

			select m.* from T_MODULE_INFO m inner join T_MODULE_ROLE mr on m.module_id = mr.module_id  where module_url = #{moduleId}


			AND role_id IN  

                #{item}

mapper.insert:

insert into T_MODULE_INFO (
	module_id , module_name , module_url , modify_no , oper_no , status_flag , need_flag
)values(
	#{moduleId} , #{moduleName} , #{moduleUrl} , #{modifyNo} , #{operNo} , '1' , #{needFlag}
)

mapper.update:

update T_MODULE_INFO set 
oper_time = sysdate , 
oper_no = #{operNo} 



	, module_name = #{moduleName}



	, module_url = #{moduleUrl}



	, status_flag = #{statusFlag}



	, need_flag = #{needFlag}



	where module_id = #{moduleId}
